package day06;

public class ArrayTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[] score = new int[5];
		score[0] = 10;
		score[1] = 20;
		score[2] = 30;
		score[3] = 40;
		score[4] = 50;

		for (int i = 0; i < score.length; i++) {
			System.out.println(score[i]);
		}
	}

}